template.content:
import request from '@/router/axios';
import {isValidateUnique, toStr} from "@/util/validate";

let gateway="/codegen";
export function page${ClassName}(query) {
  return request({
    url: gateway+'/${urlPrefix}/',
    method: 'get',
    params: query
  })
}

export function save${ClassName}(obj) {
  return request({
    url: gateway+'/${urlPrefix}/',
    method: 'post',
    data: obj
  })
}

export function find${ClassName}(id) {
  return request({
    url: gateway+'/${urlPrefix}/' + id,
    method: 'get'
  })
}

export function remove${ClassName}(id) {
  return request({
    url: gateway+'/${urlPrefix}/' + id,
    method: 'delete'
  })
}

export function validateUnique${ClassName}(rule, value, callback, id) {
  isValidateUnique(rule, value, callback, gateway+'/${urlPrefix}/checkByProperty?id='+toStr(id))
}
